import React, { useRef, useState, useCallback, useEffect } from 'react';
import './App.css';
//import Webcam from 'webcam-easy';
//import WebcamImage from "./WebcamImage";
import Webcam from "react-webcam";
import { useSpeechSynthesis } from 'react-speech-kit';


function App() {

  const [img, setImg] = useState(null);
  const webcamRef = useRef(null);

  const videoConstraints = {
    width: 420,
    height: 420,
    facingMode: "user",
  };

  const capture = useCallback(() => {
    const imageSrc = webcamRef.current.getScreenshot();
    setImg(imageSrc);
    setStatusMessage('Sending request...');
    setUploadProgress(10); // Initial progress
    callGPT4(imageSrc);
  }, [webcamRef]);

  // const {speak} = useSpeechSynthesis();
  // const readOutText = (textToRead) => {
  //   speak({text:textToRead})
  //   alert("after speak")
  // }

  const talkmethod = (textToRead) => {
    const msg = new SpeechSynthesisUtterance();
    msg.text = textToRead;
    window.speechSynthesis.speak(msg);
  }



  const [file, setFile] = useState(null);
  const [preview, setPreview] = useState('');
  const [result, setResult] = useState('');
  const [statusMessage, setStatusMessage] = useState('');
  const [uploadProgress, setUploadProgress] = useState(0);
  const [dragOver, setDragOver] = useState(false);
  //const [isCameraOpen, setIsCameraOpen] = useState(false);
  const webcamElement = document.getElementById('webcam');
const canvasElement = document.getElementById('canvas');
const snapSoundElement = document.getElementById('snapSound');
const webcam = new Webcam(webcamElement, 'user', canvasElement, snapSoundElement);
let isCamOpen = true;
  const handleFileChange = useCallback((selectedFile) => {
    setFile(selectedFile);
    setPreview(URL.createObjectURL(selectedFile));
    setStatusMessage('Image selected. Click "Analyze Image" to proceed.');
    setUploadProgress(0);
  }, []);
  // useEffect(()=>{
  //     webcam.start().then(()=>{
  //     });
  //   return () => {
  //     webcam.stop();
  //   };
  // },[]);
  // const handleBeforeUnload = () =>{
  //   console.log("stop camera");
  //   webcam.stop();
  // }
  const handleSnap = () => {
      const picture = webcam.snap();
      console.log(picture);
      document.querySelector('#download-photo').href = picture;
      webcam.stop();
  }
  const openCamera = () =>{
    webcam.start();
  }

  const callGPT4 = async (imageString) => {
    const base64String5 = imageString.replace('data:', '').replace(/^.+,/, '');

    const data1 = {
      model: "gpt-4-vision-preview",
      messages: [
        {
          "role": "user",
          "content": [
            {
              "type": "text",
              "text": "Imagine that I am a visual impaired individual. Could you explain what you see in this image as if I am seeing the picture?"
            },
            {
              "type": "image_url",
              "image_url": {
                "url": `data:image/jpeg;base64,${base64String5}`
              }
            }
          ]
        }
      ],
      max_tokens: 100
    };

    try {
      const response = await fetch('https://api.openai.com/v1/chat/completions', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer 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` // Use environment variable for API key
        },
        body: JSON.stringify(data1)
      });
      setUploadProgress(50); // Midway progress
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const apiResponse = await response.json();
      setUploadProgress(100); // Final progress
      if (apiResponse.choices && apiResponse.choices.length > 0) {
        talkmethod(apiResponse.choices[0].message.content);
        setResult(apiResponse.choices[0].message.content);
        setStatusMessage('Analysis complete.');     
      } else {
        console.error('No choices returned from API');
        setStatusMessage('Failed to get a response from the API.');
      }
    } catch (error) {
      console.error('Error:', error);
      alert(error);
      setStatusMessage('An error occurred during the analysis.');
    }
    //readOutText(result);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    if (!file) {
      //setStatusMessage('No file selected!');
      setStatusMessage('Sending request...');
      setUploadProgress(10); // Initial progress
      callGPT4();
      return;
    }
    setStatusMessage('Sending request...');
    setUploadProgress(10); // Initial progress
    const reader = new FileReader();
    reader.readAsDataURL(file);
    reader.onload = async () => {
      // const base64String = reader.result
      //   .replace('data:', '')
      //   .replace(/^.+,/, '');
      const base64String2 = img.replace('data:', '').replace(/^.+,/, '');
      //alert(base64String);
      //alert(base64String2);
      const data = {
        model: "gpt-4-vision-preview",
        messages: [
          {
            "role": "user",
            "content": [
              {
                "type": "text",
                "text": "Imagine that I am a visual impaired individual. Could you explain what you see in this image as if I am seeing the picture?"
              },
              {
                "type": "image_url",
                "image_url": {
                  "url": `data:image/jpeg;base64,${base64String2}`
                }
              }
            ]
          }
        ],
        max_tokens: 300
      };
      try {
        const response = await fetch('https://api.openai.com/v1/chat/completions', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer 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` // Use environment variable for API key
          },
          body: JSON.stringify(data)
        });
        setUploadProgress(50); // Midway progress
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        const apiResponse = await response.json();
        setUploadProgress(100); // Final progress
        if (apiResponse.choices && apiResponse.choices.length > 0) {
          setResult(apiResponse.choices[0].message.content);
          setStatusMessage('Analysis complete.');
        } else {
          console.error('No choices returned from API');
          setStatusMessage('Failed to get a response from the API.');
        }
      } catch (error) {
        console.error('Error:', error);
        alert(error);
        setStatusMessage('An error occurred during the analysis.');
      }
    };
    // reader.onerror = (error) => {
    //   console.error('Error:', error);
    //   setStatusMessage('File reading failed!');
    // };



  };
  const handleDragOver = useCallback((event) => {
    event.preventDefault();
    setDragOver(true);
  }, []);
  const handleDragLeave = useCallback(() => {
    setDragOver(false);
  }, []);
  const handleDrop = useCallback((event) => {
    event.preventDefault();
    setDragOver(false);
    const files = event.dataTransfer.files;
    if (files.length) {
      handleFileChange(files[0]);
    }
  }, [handleFileChange]);
  return (
    <div className="App">
      <h1>Sight</h1>
      <div className="Container">
      {img === null ? (
        <>
          <Webcam
            audio={false}
            mirrored={true}
            height={400}
            width={400}
            ref={webcamRef}
            screenshotFormat="image/jpeg"
            videoConstraints={videoConstraints}
          />
          <button onClick={capture}>Capture photo</button>
        </>
      ) : (
        <>
          <img src={img} alt="screenshot" />
          <button onClick={() => setImg(null)}>Retake</button>
        </>
      )}
    </div>
      {/* <div
        className={`drop-area ${dragOver ? 'drag-over' : ''}`}
        onDrop={handleDrop}
        onDragOver={handleDragOver}
        onDragLeave={handleDragLeave}
        onClick={() => document.getElementById('fileUpload').click()}
      >
        <input
          id="fileUpload"
          type="file"
          onChange={(e) => handleFileChange(e.target.files[0])}
          accept="image/*"
          style={{ display: 'none' }}
        />
        {preview ? (
          < img src={preview} alt="Preview" className="image-preview" />
        ) : (
          <p>Drag and drop an image here, or click to select an image to upload.</p >
        )}
      </div> */}
      {statusMessage && <p className="status-message">{statusMessage}</p >}
      {uploadProgress > 0 && (
        <progress value={uploadProgress} max="100"></progress>
      )}

      {/* <button onClick={handleSnap} className='webcam-button'>Snap</button>
      <button onClick={openCamera} className='webcam-button'>Open Camera</button> */}




      {/* <button onClick={handleGetPhoto} className='webcam-button'>Get Photo</button> */}
      <p></p>
      <button onClick={handleSubmit} className="analyze-button">
        Analyze Image
      </button>
      <button className='readout-button' onClick={() =>{readOutText(result)}} >Read Out</button>
      {result && (
        <div className="result">
          <strong>Analysis Result:</strong>
          <textarea value={result} readOnly />
        </div>
      )}
    </div>
  );
}
export default App;
